(()=>{"use strict";(t=>{const e=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),m=document.getElementById("timer-seconds");let s;const r=()=>{let t=(()=>{let t=(new Date("27 september 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,days:Math.floor(t/60/60/24),hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();const r=t=>t<10?`0${t} `:t;e.textContent=r(t.days),n.textContent=r(t.hours),o.textContent=r(t.minutes),m.textContent=r(t.seconds),t.timeRemaining<=0&&(clearInterval(s),e.textContent="00",n.textContent="00",o.textContent="00",m.textContent="00")};r(),s=setInterval(r,1e3)})()})();